INSERT INTO `branch_version` (`branch_code`, `branch_version`, `title`, `title_en`, `description`, `status`, `user_id`, `username`, `created_by`, `created_by_id`, `created_time`, `updated_by`, `updated_by_id`, `updated_time`) VALUES
('5214', 1, 'Khoa học giáo dục và đào tạo giáo viên', 'Electronic', 'Công nghệ thông tin (IT) ngày càng trở thành lựa chọn của nhiều bạn trẻ nhiệt huyết, yêu thích lĩnh vực công nghệ. Muốn tạo nên sự khác biệt tích cực cho thế giới thì IT là một trong những con đường lựa chọn đúng đắn của bạn bởi IT ngày nay là công cụ quan trọng không thể thiếu trong các lĩnh vực khoa học công nghệ.\r\nCơ hội việc làm của Công nghệ thông tin cho bạn rất nhiều lựa chọn hấp dẫn :\r\n- Lập trình viên: người trực tiếp tạo ra các sản phẩm công nghệ như phần mềm, hệ thống thông tin;\r\n- Kiểm duyệt chất lượng phần mềm: trực tiếp kiểm tra chất lượng các sản phẩm công nghệ do lập trình viên tạo ra;\r\n- Chuyên viên phân tích thiết kế hệ thống, quản lý dữ liệu, quản trị mạng, kỹ thuật phần cứng máy tính;\r\n- Chuyên gia quản lý, kinh doanh, điều phối các dự án công nghệ thông tin;\r\n- Giảng dạy và nghiên cứu về công nghệ thông tin tại các cơ sở đào tạo…\r\nHọc ngành Công nghệ thông tin làm việc ở đâu?\r\nTốt nghiệp ngành Công nghệ thông tin bạn có thể làm việc tại:\r\n- Các công ty, tập đoàn về công nghệ thông tin;  \r\n- Các công ty sản xuất, lắp ráp, sửa chữa trang thiết bị phần cứng;\r\n- Các công ty cung cấp giải pháp tích hợp;\r\n- Các công ty cung cấp giải pháp về mạng và an ninh mạng;\r\n- Bộ phận Quản trị, bộ phận IT tại các công ty, kể cả công ty hoạt động trong lĩnh vực Công nghệ và các lĩnh vực khác như ngân hàng, y tế, giáo dục, giải trí...\r\n- Giảng viên các trường đại học, cao đẳng, học viên, trung tâm có đào tạo Công nghệ thông tin.\r\nThống kê của Bộ Thông tin - Truyền thông, đến năm 2020, nhân lực trong lĩnh vực IT mỗi năm tăng 13%. Theo đó, lĩnh vực Công nghệ thông tin ở Việt Nam cần đến 1 triệu lao động hoạt động. Song song đó, một sự ưu ái khác của thị trường lao động đối với ngành này là thực tế các cuộc khủng hoảng kinh tế gần đây đã minh chứng, nhân lực thuộc lĩnh vực công nghệ thông tin là một trong những ngành ít chịu sự tác động nhất.\r\nNhư vậy, để thành công với những công việc trên đòi hỏi bạn phải lựa chọn cho bản thân một chương trình đào tạo Công nghệ thông tin uy tín ở các trường đại học phù hợp như Đại học Bách khoa Tp.HCM, Đại học Khoa học tự nhiên, Đại học Kinh tế Tài chính Tp.HCM (UEF), Đại học Công nghệ Tp.HCM - HUTECH, Đại học Công nghệ thông tin...', 1, NULL, '', 'admin', 1, '2016-08-08 09:58:40', 'admin', 1, '2016-08-08 09:57:39'),
('521401', 1, 'Khoa học giáo dục', 'Economy', 'Công nghệ thông tin (IT) ngày càng trở thành lựa chọn của nhiều bạn trẻ nhiệt huyết, yêu thích lĩnh vực công nghệ. Muốn tạo nên sự khác biệt tích cực cho thế giới thì IT là một trong những con đường lựa chọn đúng đắn của bạn bởi IT ngày nay là công cụ quan trọng không thể thiếu trong các lĩnh vực khoa học công nghệ.\r\nCơ hội việc làm của Công nghệ thông tin cho bạn rất nhiều lựa chọn hấp dẫn :\r\n- Lập trình viên: người trực tiếp tạo ra các sản phẩm công nghệ như phần mềm, hệ thống thông tin;\r\n- Kiểm duyệt chất lượng phần mềm: trực tiếp kiểm tra chất lượng các sản phẩm công nghệ do lập trình viên tạo ra;\r\n- Chuyên viên phân tích thiết kế hệ thống, quản lý dữ liệu, quản trị mạng, kỹ thuật phần cứng máy tính;\r\n- Chuyên gia quản lý, kinh doanh, điều phối các dự án công nghệ thông tin;\r\n- Giảng dạy và nghiên cứu về công nghệ thông tin tại các cơ sở đào tạo…\r\nHọc ngành Công nghệ thông tin làm việc ở đâu?\r\nTốt nghiệp ngành Công nghệ thông tin bạn có thể làm việc tại:\r\n- Các công ty, tập đoàn về công nghệ thông tin;  \r\n- Các công ty sản xuất, lắp ráp, sửa chữa trang thiết bị phần cứng;\r\n- Các công ty cung cấp giải pháp tích hợp;\r\n- Các công ty cung cấp giải pháp về mạng và an ninh mạng;\r\n- Bộ phận Quản trị, bộ phận IT tại các công ty, kể cả công ty hoạt động trong lĩnh vực Công nghệ và các lĩnh vực khác như ngân hàng, y tế, giáo dục, giải trí...\r\n- Giảng viên các trường đại học, cao đẳng, học viên, trung tâm có đào tạo Công nghệ thông tin.\r\nThống kê của Bộ Thông tin - Truyền thông, đến năm 2020, nhân lực trong lĩnh vực IT mỗi năm tăng 13%. Theo đó, lĩnh vực Công nghệ thông tin ở Việt Nam cần đến 1 triệu lao động hoạt động. Song song đó, một sự ưu ái khác của thị trường lao động đối với ngành này là thực tế các cuộc khủng hoảng kinh tế gần đây đã minh chứng, nhân lực thuộc lĩnh vực công nghệ thông tin là một trong những ngành ít chịu sự tác động nhất.\r\nNhư vậy, để thành công với những công việc trên đòi hỏi bạn phải lựa chọn cho bản thân một chương trình đào tạo Công nghệ thông tin uy tín ở các trường đại học phù hợp như Đại học Bách khoa Tp.HCM, Đại học Khoa học tự nhiên, Đại học Kinh tế Tài chính Tp.HCM (UEF), Đại học Công nghệ Tp.HCM - HUTECH, Đại học Công nghệ thông tin...', 1, NULL, '', 'admin', 1, '2016-08-08 09:58:36', 'admin', 1, '2016-08-08 09:58:47'),
