INSERT INTO `school_version` (`school_code`, `school_version`, `title`, `title_en`, `address`, `description`, `mobile`, `email`, `website`, `facebook`, `logo_version`, `status`, `user_id`, `username`, `created_by`, `created_by_id`, `created_time`, `updated_by`, `updated_by_id`, `updated_time`) VALUES
('BKA', 1, 'Mầm non Bách Khoa', 'Mamnon Bachkhoa', 'Số 01 Đại Cồ Việt.', 'Trường mầm non số 1 Đại Cồ Việt.Định hướng phát triển nhà trường\n\nĐể đạt được mục tiêu "Xây dựng trường Đại học Bách Khoa Hà Nội thành trường đại học đào tạo trình độ cao, đa ngành, đa lĩnh vực; một trung tâm nghiên cứu khoa học công nghệ hàng đầu của đất nước, với một số lĩnh vực đạt trình độ tiên tiến trong khu vực và trên thế giới; một địa chỉ tin cậy, hấp dẫn đối với các nhà đầu tư phát triển công nghệ, giới doanh nghiệp trong và ngoài nước", trường đại học Bách Khoa Hà Nội đã đề ra các định hướng phát triển trong thời gian tới. \n\nLịch sử, truyền thống và những trang vàng\n\nĐại học Bách khoa Hà Nội là trường đại học đa ngành về kỹ thuật được thành lập ở Hà Nội ngày 15 tháng 10 năm 1956. Trường luôn là một trong những trường đại học kỹ thuật hàng đầu của nền giáo dục Việt Nam với bề dày lịch sử. Với nhiều đóng góp cho công cuộc xây dựng và bảo vệ Tổ quốc, Trường đã được Đảng và Nhà nước tặng nhiều danh hiệu và phần thưởng quý giá cho các cá nhân và tập thể được thể hiện qua những trang vàng truyền thống của trường . \n\nĐội ngũ cán bộ\n\nTổng số cán bộ, công chức gồm 1950 cán bộ, với 1192 giảng viên và 394 cán bộ phục vụ giảng dạy và NCKH.\n\nNhà giáo nhân dân và Nhà giáo ưu tú: 154\nViện sỹ: 3\nGiáo sư và Phó giáo sư: 399\nTiến sỹ khoa học và Tiến sỹ: 703\nThạc sỹ: 1200\nĐây là đội ngũ cán bộ có uy tín, kinh nghiệm, nhiệt huyết trong hoạt động đào tạo nghiệp vụ và quản lý, trong nghiên cứu khoa học và chuyển giao công nghệ.', '0988640637', 'bachkhoa@gmail.com', 'bachkhoa.vn', 'facebook.com/bachkhoa', 1, 1, NULL, '', 'admin', 1, '2016-08-05 10:49:28', 'admin', 1, '2016-08-05 10:49:32'),
